import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./../styles/Home.css";
import defaultProfileIcon from "./../assets/profile.png";

const Home = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);
  }, []);

  return (
    <div className="home-page">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="nav-left">
          <button onClick={() => navigate("/")}>Home</button>
          <button onClick={() => navigate("/our-artisans")}>Meet Our Artisans</button>
          <button onClick={() => navigate("/events")}>Art Events</button>
        </div>
        <div className="nav-right">
          {user ? (
            <img
              src={user.profilePicture || defaultProfileIcon}
              alt="Profile"
              className="profile-icon"
              onClick={() => navigate("/profile")}
            />
          ) : (
            <button className="login-btn" onClick={() => navigate("/login")}>
              Sign In
            </button>
          )}
        </div>
      </nav>

      {/* Animated Hero Section */}
      <div className="hero-section">
        <h1 className="animated-logo">
          <span>Artisans</span> Connect</h1>
        <p className="tagline">Connecting Artisans with the World</p>
      </div>

      {/* Consumer Card */}
      <div className="register-card consumer-card" onClick={() => navigate("/Signup")}>
        <div className="card-overlay">
          <h2>Be A Consumer</h2>
          <p>Learn art from courses, Buy art products, and explore creativity.</p>
        </div>
      </div>

      {/* Artisan Card */}
      <div className="register-card artisan-card" onClick={() => navigate("/Signup")}>
        <div className="card-overlay">
          <h2>Be an Artisan</h2>
          <p>Showcase your art to the world and start selling your creativity.</p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2025 Artisans Connect. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
