import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserCog, Palette } from 'lucide-react';
import './../styles/Home.css';

const Home = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const logoRef = useRef(null);
  
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-logo');
          }
        });
      },
      { threshold: 0.1 }
    );
  
    // Store the current value of logoRef
    const currentLogoRef = logoRef.current;
  
    if (currentLogoRef) {
      observer.observe(currentLogoRef);
    }
  
    return () => {
      if (currentLogoRef) {
        observer.unobserve(currentLogoRef);
      }
    };
  }, []);

  const NavBar = () => (
    <nav className="navbar">
      <div className="navbar-left">
        <button 
          className="nav-button active" 
          onClick={() => navigate('/')}
        >
          Home
        </button>
        <button 
          className="nav-button" 
          onClick={() => navigate('/our-artisans')}
        >
          Meet Our Artisans
        </button>
        <button 
          className="nav-button" 
          onClick={() => navigate('/events')}
        >
          Art Events
        </button>
      </div>
      <div className="navbar-right">
        {user ? (
          <div className="user-profile" onClick={() => navigate('/profile')}>
            <img 
              src={user.profilePicture || '/assets/default-profile.png'} 
              alt="Profile" 
              className="profile-icon" 
            />
            <span className="user-name">{user.name}</span>
          </div>
        ) : (
          <button className="sign-in-button" onClick={() => navigate('/login')}>
            <UserCog size={18} />
            <span>Sign In</span>
          </button>
        )}
      </div>
    </nav>
  );

  const HeroSection = () => (
    <div className="hero-section">
      <div className="hero-content">
        <div className="logo-container" ref={logoRef}>
          <Palette size={80} className="logo-icon" />
          <div className="logo-text">
            <h1 className="hero-title">
              <span className="gradient-text">Artisans</span> Connect
            </h1>
          </div>
        </div>
        <p className="hero-tagline">Connecting Artisans with the World</p>
        <div className="hero-background-shapes">
          <div className="shape shape-1"></div>
          <div className="shape shape-2"></div>
          <div className="shape shape-3"></div>
        </div>
      </div>
    </div>
  );

  const RegisterCard = ({ title, description, imageSrc, buttonText, navigateTo, isReversed = false }) => (
    <div 
      className={`register-card ${isReversed ? 'register-card-reversed' : ''}`}
      style={{ backgroundImage: `url(${imageSrc})` }}
    >
      <div className="register-card-overlay"></div>
      <div className="register-card-content">
        <h2 className="register-card-title">
          <span className="gradient-text">{title}</span>
        </h2>
        <p className="register-card-description">{description}</p>
        <button 
          className="register-button" 
          onClick={() => navigate(navigateTo)}
        >
          {buttonText}
        </button>
      </div>
    </div>
  );

  return (
    <div className="home-container">
      <NavBar />
      <HeroSection />
      
      <div className="register-cards-container">
        <RegisterCard 
          title="Be A Consumer"
          description="Learn art from courses, discover unique handmade products, and connect with talented artisans from around the world."
          imageSrc="https://images.pexels.com/photos/1497302/pexels-photo-1497302.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
          buttonText="Register as Consumer"
          navigateTo="/register/consumer"
        />
        
        <RegisterCard 
          title="Be an Artisan"
          description="Showcase your art to the world. Connect with potential buyers, share your creative process, and grow your business."
          imageSrc="https://images.pexels.com/photos/3094218/pexels-photo-3094218.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
          buttonText="Register as Artisan"
          navigateTo="/register/artisan"
          isReversed={true}
        />
      </div>
      
      <footer className="footer">
        <p className="copyright">&copy; 2025 Artisan's Connect. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
