import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./../../styles/CourseManage.css";

const CourseManage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [type, setType] = useState("online");
  const [files, setFiles] = useState([]);
  const [venue, setVenue] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [contentExists, setContentExists] = useState(false);
  const [message, setMessage] = useState("");
  const [consumers, setConsumers] = useState([]);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [courseRes, contentRes, consumerRes] = await Promise.all([
          axios.get(`http://localhost:5000/api/courses/${courseId}`),
          axios.get(`http://localhost:5000/api/CourseContent/${courseId}/content`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get(`http://localhost:5000/api/artisans/my-courses-with-consumers`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setCourse(courseRes.data);
        setContentExists(true);
        setMessage("Content already uploaded for this course.");

        const courseConsumer = consumerRes.data.find(c => c._id === courseId);
        setConsumers(courseConsumer?.consumers || []);
      } catch (err) {
        setContentExists(false);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [courseId]);

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("type", type);
      if (type === "offline") {
        formData.append("venue", venue);
        formData.append("date", date);
        formData.append("time", time);
      } else {
        for (let i = 0; i < files.length; i++) {
          formData.append("files", files[i]);
        }
      }

      await axios.post(
        `http://localhost:5000/api/CourseContent/${courseId}/content`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage("‚úÖ Content uploaded successfully!");
      setContentExists(true);
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå Upload failed.");
    }
  };

  const getTotalEarnings = () => {
    return course?.price && consumers?.length
      ? `‚Çπ${course.price * consumers.length}`
      : "‚Çπ0";
  };

  if (loading) return <div className="manage-container">Loading...</div>;

  return (
    <div className="manage-container">
      <div className="breadcrumb">
        <span onClick={() => navigate("/artisan/courses")}>Dashboard &gt; Courses</span>
        <strong> &gt; Manage "{course?.title}"</strong>
      </div>

      <h2>Manage Course: {course?.title}</h2>
      <p className="message">{message}</p>

      <div className="overview-box">
        <img src={course?.image} alt="Course" />
        <div>
          <h3>{course?.title}</h3>
          <p>{course?.description}</p>
          <p><strong>Type:</strong> {course?.type}</p>
          <p><strong>Total Enrolled:</strong> {consumers.length}</p>
          <p><strong>Total Earnings:</strong> {getTotalEarnings()}</p>
        </div>
      </div>

      {!contentExists && (
        <form onSubmit={handleUpload} className="manage-form">
          <label>Course Type:</label>
          <select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="online">Online</option>
            <option value="offline">Offline</option>
          </select>

          {type === "online" && (
            <div>
              <label>Upload Files (PDF, Image, Video):</label>
              <input type="file" multiple onChange={(e) => setFiles(e.target.files)} />
            </div>
          )}

          {type === "offline" && (
            <>
              <label>Venue:</label>
              <input type="text" value={venue} onChange={(e) => setVenue(e.target.value)} />
              <label>Date:</label>
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
              <label>Time:</label>
              <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
            </>
          )}

          <button type="submit">Upload Content</button>
        </form>
      )}

      <div className="consumer-section">
        <h3>üìã Enrolled Consumers</h3>
        {consumers.length === 0 ? (
          <p>No consumers enrolled yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Purchase Date</th>
              </tr>
            </thead>
            <tbody>
              {consumers.map((c, i) => (
                <tr key={i}>
                  <td>{c.name}</td>
                  <td>{c.email?.replace(/(.{2}).+(@.+)/, "$1****$2")}</td>
                  <td>{new Date(c.purchaseDate).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <button className="back-btn" onClick={() => navigate("/artisan/courses")}>‚Üê Back to Courses</button>
    </div>
  );
};

export default CourseManage;
