import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import "./../../styles/ManageCourse.css";

const ManageCourse = () => {
  const { courseId } = useParams();
  const [contentList, setContentList] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [files, setFiles] = useState([]);
  const [contentName, setContentName] = useState("");
  const [contentDescription, setContentDescription] = useState("");
  const [assignmentFile, setAssignmentFile] = useState(null);
  const [assignmentTitle, setAssignmentTitle] = useState("");
  const [assignmentDescription, setAssignmentDescription] = useState("");
  const [deadline, setDeadline] = useState("");
  const token = localStorage.getItem("token");

  // New state for UI control
  const [activeTab, setActiveTab] = useState("content");
  const [showContentForm, setShowContentForm] = useState(false);
  const [showAssignmentForm, setShowAssignmentForm] = useState(false);
  const [viewingSubmissions, setViewingSubmissions] = useState(null);

  // New state for editing content
  const [editingContentId, setEditingContentId] = useState(null);
  const [updatedContentName, setUpdatedContentName] = useState("");
  const [updatedContentDescription, setUpdatedContentDescription] = useState("");
  const [updatedFiles, setUpdatedFiles] = useState([]);

  // Fetch course content
  const fetchContent = useCallback(async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/coursecontent/public/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) {
        console.error("Failed to fetch content:", res.status);
        return;
      }

      const data = await res.json();
      setContentList(data);
    } catch (err) {
      console.error("Error fetching content", err);
    }
  }, [courseId, token]);

  // Fetch assignments
  const fetchAssignments = useCallback(async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/assignments/${courseId}/assignments`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setAssignments(data);
    } catch (err) {
      console.error("Error fetching assignments", err);
    }
  }, [courseId, token]);

  useEffect(() => {
    fetchContent();
    fetchAssignments();
  }, [fetchContent, fetchAssignments]);

  // Handle content upload
  const handleContentUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Array.from(files).forEach((file) => {
      formData.append("files", file);
    });
    formData.append("name", contentName);
    formData.append("description", contentDescription);

    try {
      const res = await fetch(`http://localhost:5000/api/coursecontent/${courseId}`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });
      if (res.ok) {
        setFiles([]);
        setContentName("");
        setContentDescription("");
        setShowContentForm(false);
        fetchContent();
      } else {
        const errorData = await res.json();
        alert(errorData.message || "Upload failed");
      }
    } catch (err) {
      console.error("Error uploading content", err);
    }
  };

  // Handle content deletion
  const handleDeleteContent = async (id) => {
    if (!window.confirm("Delete this content?")) return;
    try {
      await fetch(`http://localhost:5000/api/coursecontent/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchContent();
    } catch (err) {
      console.error("Error deleting content", err);
    }
  };

  // Handle content editing
  const handleEditContent = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    // Append updated files if any
    Array.from(updatedFiles).forEach((file) => {
      formData.append("files", file);
    });

    formData.append("name", updatedContentName);
    formData.append("description", updatedContentDescription);

    try {
      const res = await fetch(`http://localhost:5000/api/coursecontent/${editingContentId}`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (res.ok) {
        setEditingContentId(null);
        setUpdatedContentName("");
        setUpdatedContentDescription("");
        setUpdatedFiles([]);
        fetchContent();
      } else {
        const errorData = await res.json();
        alert(errorData.message || "Update failed");
      }
    } catch (err) {
      console.error("Error updating content", err);
    }
  };

  return (
    <div className="manage-container">
      <div className="breadcrumb">
        <span onClick={() => window.history.back()}>Courses</span> &gt; Manage Course
      </div>

      {/* Navigation Bar */}
      <div className="course-navbar">
        <div
          className={`nav-item ${activeTab === "content" ? "active" : ""}`}
          onClick={() => setActiveTab("content")}
        >
          Course Content
        </div>
        <div
          className={`nav-item ${activeTab === "assignments" ? "active" : ""}`}
          onClick={() => setActiveTab("assignments")}
        >
          Assignments
        </div>
      </div>

      {/* Course Content Section */}
      {activeTab === "content" && (
        <div className="content-section">
          <div className="section-header">
            <h2>Course Content</h2>
            <button
              className="action-btn"
              onClick={() => setShowContentForm(!showContentForm)}
            >
              {showContentForm ? "Cancel" : "Upload New Content"}
            </button>
          </div>

          {/* Content Upload Form */}
          {showContentForm && (
            <div className="form-container">
              <h3>Upload New Content</h3>
              <form onSubmit={handleContentUpload} className="manage-form">
                <div className="form-group">
                  <label>Content Name:</label>
                  <input
                    type="text"
                    value={contentName}
                    onChange={(e) => setContentName(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Content Description:</label>
                  <textarea
                    value={contentDescription}
                    onChange={(e) => setContentDescription(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Upload Course Files (PDF, Video, Image):</label>
                  <input type="file" multiple onChange={(e) => setFiles(e.target.files)} />
                </div>

                <button type="submit">Upload Content</button>
              </form>
            </div>
          )}

          {/* Edit Content Form */}
          {editingContentId && (
            <div className="form-container">
              <h3>Edit Content</h3>
              <form onSubmit={handleEditContent} className="manage-form">
                <div className="form-group">
                  <label>Content Name:</label>
                  <input
                    type="text"
                    value={updatedContentName}
                    onChange={(e) => setUpdatedContentName(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Content Description:</label>
                  <textarea
                    value={updatedContentDescription}
                    onChange={(e) => setUpdatedContentDescription(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Upload New Files (Optional):</label>
                  <input type="file" multiple onChange={(e) => setUpdatedFiles(e.target.files)} />
                </div>

                <button type="submit">Update Content</button>
                <button
                  type="button"
                  onClick={() => {
                    setEditingContentId(null);
                    setUpdatedContentName("");
                    setUpdatedContentDescription("");
                    setUpdatedFiles([]);
                  }}
                >
                  Cancel
                </button>
              </form>
            </div>
          )}

          {/* Content List */}
          <div className="list-container">
            {contentList.length === 0 ? (
              <p className="empty-message">No content uploaded yet.</p>
            ) : (
              <ul className="content-list">
                {contentList.map((item) => (
                  <li key={item._id} className="content-item">
                    <div className="content-header">
                      <h4>{item.name}</h4>
                      <button
                        className="edit-btn"
                        onClick={() => {
                          setEditingContentId(item._id);
                          setUpdatedContentName(item.name);
                          setUpdatedContentDescription(item.description);
                        }}
                      >
                        Edit Content
                      </button>
                      <button
                        className="delete-btn"
                        onClick={() => handleDeleteContent(item._id)}
                      >
                        Delete
                      </button>
                    </div>
                    <p className="content-description">{item.description}</p>
                    <div className="content-files">
                      {item.files && item.files.length > 0 ? (
                        item.files.map((file, index) => (
                          <a
                            key={index}
                            href={`http://localhost:5000/${file}`}
                            target="_blank"
                            rel="noreferrer"
                            className="file-link"
                          >
                            {file.split("/").pop()}
                          </a>
                        ))
                      ) : (
                        <span className="no-file">No file available</span>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageCourse;
