import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { MapContainer, TileLayer, Marker, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-defaulticon-compatibility";

const BuyProduct = () => {
  const { productId } = useParams();
  const navigate = useNavigate();

  // State for address fields
  const [flatNo, setFlatNo] = useState("");
  const [nearbyLocation, setNearbyLocation] = useState("");
  const [city, setCity] = useState("");
  const [pincode, setPincode] = useState("");
  const [state, setState] = useState("");
  const [country, setCountry] = useState("");
  const [contact, setContact] = useState("");
  const [error, setError] = useState("");
  const [artisanDetails, setArtisanDetails] = useState(null);

  // State for map and location
  const [showMap, setShowMap] = useState(false);
  const [selectedPosition, setSelectedPosition] = useState([51.505, -0.09]); // Default position (London)

  // Function to fetch address from coordinates
  const fetchAddressFromCoordinates = async (latitude, longitude) => {
    try {
      const res = await axios.get(
        `https://api.opencagedata.com/geocode/v1/json?q=${latitude}+${longitude}&key=YOUR_OPENCAGE_API_KEY`
      );

      if (res.data.results.length > 0) {
        const components = res.data.results[0].components;

        // Extract address components
        setFlatNo(components.house_number || "");
        setNearbyLocation(components.road || "");
        setCity(components.city || components.town || components.village || "");
        setPincode(components.postcode || "");
        setState(components.state || "");
        setCountry(components.country || "");
      } else {
        alert("Unable to fetch location details.");
      }
    } catch (error) {
      console.error("Error fetching location:", error);
      alert("Failed to fetch location. Please try again.");
    }
  };

  // Map click handler
  const LocationMarker = () => {
    useMapEvents({
      click(e) {
        const { lat, lng } = e.latlng;
        setSelectedPosition([lat, lng]);
        fetchAddressFromCoordinates(lat, lng);
      },
    });

    return <Marker position={selectedPosition}></Marker>;
  };

  const handleBuy = async (e) => {
    e.preventDefault();

    if (!flatNo || !nearbyLocation || !city || !pincode || !state || !country || !contact) {
      setError("Please fill in all required fields.");
      return;
    }

    const address = `${flatNo}, ${nearbyLocation}, ${city}, ${state}, ${country} - ${pincode}`;

    try {
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/products/buy",
        { productId, contact, address },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.status === 200) {
        setArtisanDetails(res.data.artisan);
        alert("Product purchased successfully!");
        navigate("/orders");
      }
    } catch (err) {
      console.error("Error in buying product:", err);
      setError("Failed to purchase product. Please try again.");
    }
  };

  return (
    <div className="buy-product-container">
      <h1>Buy Product</h1>
      <form onSubmit={handleBuy}>
        {error && <p className="error">{error}</p>}

        <label>Contact:</label>
        <input
          type="text"
          value={contact}
          onChange={(e) => setContact(e.target.value)}
          placeholder="Enter your contact number"
          required
        />

        <h3>Address Details</h3>

        <label>Flat No:</label>
        <input
          type="text"
          value={flatNo}
          onChange={(e) => setFlatNo(e.target.value)}
          placeholder="Enter your flat number"
          required
        />

        <label>Nearby Location:</label>
        <input
          type="text"
          value={nearbyLocation}
          onChange={(e) => setNearbyLocation(e.target.value)}
          placeholder="Enter nearby location"
          required
        />

        <label>City:</label>
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter your city"
          required
        />

        <label>Pincode:</label>
        <input
          type="text"
          value={pincode}
          onChange={(e) => setPincode(e.target.value)}
          placeholder="Enter your pincode"
          required
        />

        <label>State:</label>
        <input
          type="text"
          value={state}
          onChange={(e) => setState(e.target.value)}
          placeholder="Enter your state"
          required
        />

        <label>Country:</label>
        <input
          type="text"
          value={country}
          onChange={(e) => setCountry(e.target.value)}
          placeholder="Enter your country"
          required
        />

        <button type="button" onClick={() => setShowMap(true)}>
          Choose Location
        </button>

        <button type="submit">Buy Now</button>
      </form>

      {showMap && (
        <div className="map-container">
          <h3>Select Location on Map</h3>
          <MapContainer center={selectedPosition} zoom={13} style={{ height: "400px", width: "100%" }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <LocationMarker />
          </MapContainer>
          <button onClick={() => setShowMap(false)}>Close Map</button>
        </div>
      )}

      {artisanDetails && (
        <div className="artisan-details">
          <h2>Artisan Details</h2>
          <p>Name: {artisanDetails.name}</p>
          <p>Email: {artisanDetails.email}</p>
          <p>Contact: {artisanDetails.contact}</p>
        </div>
      )}
    </div>
  );
};

export default BuyProduct;
