// components/ProfileSidebar.js
import React, { useEffect, useState } from "react";
import "./../styles/ProfileSidebar.css";

const ProfileSidebar = ({ isOpen, onClose }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (!isOpen) return;

    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");

        const res = await fetch("http://localhost:5000/api/users/me", {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json"
          },
        });

        const data = await res.json();
        console.log("Fetched profile:", data);

        if (res.ok) {
          setUser(data);
        } else {
          console.warn("Failed to load profile:", data.message);
        }
      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };

    fetchProfile();
  }, [isOpen]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    onClose();
    window.location.href = "/login";
  };

  return (
    <div className={`profile-sidebar ${isOpen ? "open" : ""}`}>
      <div className="sidebar-header">
        <h2>My Profile</h2>
        <button className="close-btn" onClick={onClose}>Ã—</button>
      </div>

      {user ? (
        <div className="profile-content">
          {user.profilePicture && (
            <img
              src={`http://localhost:5000/uploads/${user.profilePicture}`}
              alt="Profile"
              className="profile-pic"
            />
          )}
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
          <p><strong>Contact:</strong> {user.contact || "N/A"}</p>
          <p><strong>Address:</strong> {user.address || "N/A"}</p>

          <h3>Sold Products</h3>
          <ul>
            {user.soldProducts?.length > 0 ? (
              user.soldProducts.map((p) => (
                <li key={p._id}>{p.name} - â‚¹{p.price}</li>
              ))
            ) : (
              <li>No products sold yet.</li>
            )}
          </ul>

          <h3>Sold Courses</h3>
          <ul>
            {user.soldCourses?.length > 0 ? (
              user.soldCourses.map((c) => (
                <li key={c._id}>{c.name} - â‚¹{c.price}</li>
              ))
            ) : (
              <li>No courses sold yet.</li>
            )}
          </ul>

          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default ProfileSidebar;
