import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./../../styles/CourseManage.css";

const CourseManage = () => {
  const { courseId } = useParams();
  const [type, setType] = useState("online");
  const [files, setFiles] = useState([]);
  const [venue, setVenue] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [contentExists, setContentExists] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5000/api/CourseContent/${courseId}/content`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setContentExists(true);
        setMessage("Content already uploaded for this course.");
      } catch (err) {
        setContentExists(false);
      }
    };
    fetchContent();
  }, [courseId]);

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const formData = new FormData();
      formData.append("type", type);
      if (type === "offline") {
        formData.append("venue", venue);
        formData.append("date", date);
        formData.append("time", time);
      } else {
        for (let i = 0; i < files.length; i++) {
          formData.append("files", files[i]);
        }
      }

      const res = await axios.post(
        `http://localhost:5000/api/CourseContent/${courseId}/content`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setMessage("Content uploaded successfully!");
      setContentExists(true);
    } catch (err) {
      setMessage(err.response?.data?.message || "Upload failed.");
    }
  };

  return (
    <div className="manage-container">
      <h2>Manage Course Content</h2>
      <p>{message}</p>
      {!contentExists && (
        <form onSubmit={handleUpload} className="manage-form">
          <label>Course Type:</label>
          <select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="online">Online</option>
            <option value="offline">Offline</option>
          </select>

          {type === "online" && (
            <div>
              <label>Upload Files:</label>
              <input type="file" multiple onChange={(e) => setFiles(e.target.files)} />
            </div>
          )}

          {type === "offline" && (
            <>
              <label>Venue:</label>
              <input type="text" value={venue} onChange={(e) => setVenue(e.target.value)} />

              <label>Date:</label>
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />

              <label>Time:</label>
              <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
            </>
          )}

          <button type="submit">Upload Content</button>
        </form>
      )}
    </div>
  );
};

export default CourseManage;
