import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./../../styles/Courses.css";

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Please log in to view your courses.");
          setLoading(false);
          return;
        }

        const response = await fetch("http://localhost:5000/api/courses/my-courses", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.message || "Failed to fetch courses");
        }

        setCourses(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  return (
    <div className="courses-container">
      <h1>Your Courses</h1>
      <Link to="/add-courses" className="new-course-btn">+ New Course</Link>

      {loading ? (
        <p>Loading courses...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : courses.length === 0 ? (
        <p>No courses added yet.</p>
      ) : (
        <div className="courses-list">
          {courses.map((course) => (
            <div key={course._id} className="course-card">
              <img
                src={course.image ? `http://localhost:5000/${course.image}` : "https://via.placeholder.com/150"}
                alt={course.name}
                className="course-image"
              />
              <h3>{course.name}</h3>
              <p>Description:{course.description}</p>
              <p>Price: â‚¹{course.price}</p>
              <p>Mode: {course.mode}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Courses;
