import React, { useEffect, useState } from "react";
import "./../../styles/ConsumerCourses.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ConsumerCourses = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/courses/AllCourses");
        if (response.data.length === 0) {
          setError("No courses available at the moment.");
        } else {
          setCourses(response.data);
        }
      } catch (err) {
        console.error("Error fetching courses:", err);
        setError("Failed to load courses. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (loading) {
    return <div className="courses-container"><h2>Loading courses...</h2></div>;
  }

  if (error) {
    return <div className="courses-container"><h2>{error}</h2></div>;
  }

  return (
    <div className="courses-container">
      <h1>Available Courses</h1>

      <div className="courses-grid">
        {courses.map((course) => (
          <div key={course._id} className="course-card">
            <img
              src={course.image && course.image.startsWith("http") ? course.image : "http://localhost:5000/uploads/default.jpg"}
              alt={course.name}
              className="course-image"
              onError={(e) => { e.target.src = "http://localhost:5000/uploads/default.jpg"; }} // Fix broken images
            />
            <div className="course-details">
              <h2>{course.name}</h2>
              <p>Mode: {course.mode}</p>
              <p className="price">â‚¹{course.price}</p>
              <button
                className="enroll-btn"
                onClick={() => navigate(`/enroll/${course._id}`)}
              >
                Enroll
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ConsumerCourses;
