import React from "react";
import { Navigate, Outlet } from "react-router-dom";

// Function to check user authentication and role
const getUser = () => {
  const user = localStorage.getItem("user");
  return user ? JSON.parse(user) : null;
};

const PrivateRoute = ({ allowedRoles }) => {
  const user = getUser();

  if (!user) {
    return <Navigate to="pages/Login" />;
  }

  if (!allowedRoles.includes(user.role)) {
    // Redirect to the appropriate home page if the role does not match
    return user.role === "Artisan" ? <Navigate to="pages/Artisan/ArtisanHome" /> : <Navigate to="/Consumer/ConsumerHome" />;
  }

  return <Outlet />;
};

export default PrivateRoute;
