import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./../../styles/CourseView.css";

const CourseView = () => {
  const { courseId } = useParams();
  const [content, setContent] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5000/api/courses/${courseId}/content`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setContent(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch content");
      }
    };
    fetchContent();
  }, [courseId]);

  if (error) return <div className="error-msg">{error}</div>;
  if (!content) return <p>Loading course content...</p>;

  return (
    <div className="view-container">
      <h2>Course Content</h2>
      <p><strong>Mode:</strong> {content.type}</p>

      {content.type === "online" ? (
        <div className="content-files">
          {content.files.map((file, index) => (
            <div key={index} className="file-item">
              <a href={`http://localhost:5000/${file}`} target="_blank" rel="noreferrer">
                {file.split("/").pop()}
              </a>
            </div>
          ))}
        </div>
      ) : (
        <div className="offline-details">
          <p><strong>Venue:</strong> {content.venue}</p>
          <p><strong>Date:</strong> {content.date}</p>
          <p><strong>Time:</strong> {content.time}</p>
        </div>
      )}
    </div>
  );
};

export default CourseView;
