import React, { useState } from "react";
import ArtisanNavbar from "../../components/ArtisanNavbar";
import { Outlet } from "react-router-dom";
import ProfileSidebar from "../../components/ProfileSidebar"; // optional if you made one

const ArtisanLayout = () => {
  const [showProfileSidebar, setShowProfileSidebar] = useState(false);

  const toggleProfileSidebar = () => {
    setShowProfileSidebar(!showProfileSidebar);
  };

  return (
    <div>
      <ArtisanNavbar onProfileClick={toggleProfileSidebar} />

      {showProfileSidebar && <ProfileSidebar onClose={toggleProfileSidebar} />}

      <div style={{ padding: "20px" }}>
        <Outlet /> {/* ðŸ‘ˆ This is where nested route content will render */}
      </div>
    </div>
  );
};

export default ArtisanLayout;
