import React from "react";
import { Navigate, Outlet } from "react-router-dom";

// Function to check user authentication and role
const getUser = () => {
  const user = localStorage.getItem("user");
  return user ? JSON.parse(user) : null;
};

const PrivateRoute = ({ allowedRoles }) => {
  const user = getUser();

  console.log("User from localStorage:", user); // Debugging log
  console.log("Allowed Roles:", allowedRoles); // Debugging log
  console.log("User Role:", user?.role); // Debugging log

  if (!user) {
    console.log("User not authenticated. Redirecting to /Login.");
    return <Navigate to="/Login" />;
  }

  if (!allowedRoles.includes(user.role)) {
    console.log("User role does not match allowed roles. Redirecting to home page.");
    return user.role === "Artisan" ? (
      <Navigate to="/Artisan/ArtisanHome" />
    ) : (
      <Navigate to="/Consumer/ConsumerHome" />
    );
  }

  console.log("User role matches allowed roles. Rendering child routes.");
  return <Outlet />;
};

export default PrivateRoute;
