import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Auth.css"; // Shared CSS for Login & Signup

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const hasRedirected = useRef(false); // Prevent multiple redirects

  // useEffect(() => {
  //   const token = localStorage.getItem("token");
  //   if (token && !hasRedirected.current) {
  //     hasRedirected.current = true; // Mark as redirected
  //     navigate("/Consumer/ConsumerHome"); // Default to ConsumerHome
  //   }
  // }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });
      alert("Login Successful");
      localStorage.setItem("token", response.data.token);
      navigate(response.data.role === "artisan" ? "/Artisan/ArtisanHome" : "/Consumer/ConsumerHome");
    } catch (error) {
      if (error.response && error.response.data && error.response.data.message) {
        alert(error.response.data.message);
      } else {
        alert("Login Failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
      <p>
        Don't have an account? <span onClick={() => navigate("/Signup")}>Sign up</span>
      </p>
    </div>
  );
};

export default Login;
