import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import "./../../styles/ManageCourse.css";

const ManageCourse = () => {
  const { courseId } = useParams();
  const [contentList, setContentList] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [files, setFiles] = useState([]);
  const [assignmentFile, setAssignmentFile] = useState(null);
  const [deadline, setDeadline] = useState("");
  const token = localStorage.getItem("token");

  const fetchContent = useCallback(async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/course-content/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setContentList(data);
    } catch (err) {
      console.error("Error fetching content", err);
    }
  }, [courseId, token]);

  const fetchAssignments = useCallback(async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/assignments/${courseId}/assignments`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setAssignments(data);
    } catch (err) {
      console.error("Error fetching assignments", err);
    }
  }, [courseId, token]);

  useEffect(() => {
    fetchContent();
    fetchAssignments();
  }, [fetchContent, fetchAssignments]);

  // Rest of the component...
};

export default ManageCourse;
